  "584": {
    "pageNumber": 584,
    "surahs": [
      79
    ],
    "verses": [
      {
        "number": 16,
        "surah": 79,
        "juz": 30,
        "text": "Zoti i tij e thirri në luginën e shenjtë të Tuvas dhe i tha",
        "translation": "Zoti i tij e thirri në luginën e shenjtë të Tuvas dhe i tha",
        "page": 584
      },
      {
        "number": 17,
        "surah": 79,
        "juz": 30,
        "text": "“Shko te Faraoni, se ai, me të vërtetë, ka kaluar çdo kufi",
        "translation": "“Shko te Faraoni, se ai, me të vërtetë, ka kaluar çdo kufi",
        "page": 584
      },
      {
        "number": 18,
        "surah": 79,
        "juz": 30,
        "text": "dhe thuaji: “A do ta pastrosh veten",
        "translation": "dhe thuaji: “A do ta pastrosh veten",
        "page": 584
      },
      {
        "number": 19,
        "surah": 79,
        "juz": 30,
        "text": "dhe të të udhëzoj te Zoti yt, që t’i frikësohesh?”",
        "translation": "dhe të të udhëzoj te Zoti yt, që t’i frikësohesh?”",
        "page": 584
      },
      {
        "number": 20,
        "surah": 79,
        "juz": 30,
        "text": "Musai ia tregoi mrekullinë më të madhe",
        "translation": "Musai ia tregoi mrekullinë më të madhe",
        "page": 584
      },
      {
        "number": 21,
        "surah": 79,
        "juz": 30,
        "text": "por ai (Faraoni) e mohoi dhe e kundërshtoi",
        "translation": "por ai (Faraoni) e mohoi dhe e kundërshtoi",
        "page": 584
      },
      {
        "number": 22,
        "surah": 79,
        "juz": 30,
        "text": "Pastaj i ktheu shpinën, duke u përpjekur (kundër së vërtetës)",
        "translation": "Pastaj i ktheu shpinën, duke u përpjekur (kundër së vërtetës)",
        "page": 584
      },
      {
        "number": 23,
        "surah": 79,
        "juz": 30,
        "text": "dhe tuboi njerëzit e u deklaroi",
        "translation": "dhe tuboi njerëzit e u deklaroi",
        "page": 584
      },
      {
        "number": 24,
        "surah": 79,
        "juz": 30,
        "text": "“Unë jam zoti juaj më i lartë!”",
        "translation": "“Unë jam zoti juaj më i lartë!”",
        "page": 584
      },
      {
        "number": 25,
        "surah": 79,
        "juz": 30,
        "text": "Prandaj Allahu e goditi atë me dënim në jetën tjetër dhe në këtë jetë",
        "translation": "Prandaj Allahu e goditi atë me dënim në jetën tjetër dhe në këtë jetë",
        "page": 584
      },
      {
        "number": 26,
        "surah": 79,
        "juz": 30,
        "text": "Kjo është këshillë për ata që i frikësohen Allahut",
        "translation": "Kjo është këshillë për ata që i frikësohen Allahut",
        "page": 584
      },
      {
        "number": 27,
        "surah": 79,
        "juz": 30,
        "text": "Cili është më i vështirë: krijimi i juaj apo i qiellit, të cilin Ai e ndërtoi",
        "translation": "Cili është më i vështirë: krijimi i juaj apo i qiellit, të cilin Ai e ndërtoi",
        "page": 584
      },
      {
        "number": 28,
        "surah": 79,
        "juz": 30,
        "text": "ia ngriti lart kupën dhe e përsosi",
        "translation": "ia ngriti lart kupën dhe e përsosi",
        "page": 584
      },
      {
        "number": 29,
        "surah": 79,
        "juz": 30,
        "text": "Netët i bëri të errëta, kurse ditët të ndritshme",
        "translation": "Netët i bëri të errëta, kurse ditët të ndritshme",
        "page": 584
      },
      {
        "number": 30,
        "surah": 79,
        "juz": 30,
        "text": "Pastaj e shtriu rrafsh Tokën",
        "translation": "Pastaj e shtriu rrafsh Tokën",
        "page": 584
      },
      {
        "number": 31,
        "surah": 79,
        "juz": 30,
        "text": "dhe, nga ajo nxori ujërat dhe bëri kullotat",
        "translation": "dhe, nga ajo nxori ujërat dhe bëri kullotat",
        "page": 584
      },
      {
        "number": 32,
        "surah": 79,
        "juz": 30,
        "text": "kurse malet i bëri të patundshme",
        "translation": "kurse malet i bëri të patundshme",
        "page": 584
      },
      {
        "number": 33,
        "surah": 79,
        "juz": 30,
        "text": "që t’i gëzoni ju dhe bagëtitë tuaja",
        "translation": "që t’i gëzoni ju dhe bagëtitë tuaja",
        "page": 584
      },
      {
        "number": 34,
        "surah": 79,
        "juz": 30,
        "text": "Kur të arrijë gjëma më e madhe",
        "translation": "Kur të arrijë gjëma më e madhe",
        "page": 584
      },
      {
        "number": 35,
        "surah": 79,
        "juz": 30,
        "text": "atë Ditë njeriu do të kujtojë gjithçka që ka bërë",
        "translation": "atë Ditë njeriu do të kujtojë gjithçka që ka bërë",
        "page": 584
      },
      {
        "number": 36,
        "surah": 79,
        "juz": 30,
        "text": "dhe Zjarri flakërues do t’i tregohet çdokujt që sheh",
        "translation": "dhe Zjarri flakërues do t’i tregohet çdokujt që sheh",
        "page": 584
      },
      {
        "number": 37,
        "surah": 79,
        "juz": 30,
        "text": "Atëherë, ai që ka shkelur gjithçka",
        "translation": "Atëherë, ai që ka shkelur gjithçka",
        "page": 584
      },
      {
        "number": 38,
        "surah": 79,
        "juz": 30,
        "text": "dhe ka parapëlqyer jetën e kësaj bote",
        "translation": "dhe ka parapëlqyer jetën e kësaj bote",
        "page": 584
      },
      {
        "number": 39,
        "surah": 79,
        "juz": 30,
        "text": "sigurisht që do të ketë për strehë Xhehenemin",
        "translation": "sigurisht që do të ketë për strehë Xhehenemin",
        "page": 584
      },
      {
        "number": 40,
        "surah": 79,
        "juz": 30,
        "text": "Kurse ai që i është frikësuar madhërisë së Zotit të tij dhe e ka frenuar veten nga dëshirat",
        "translation": "Kurse ai që i është frikësuar madhërisë së Zotit të tij dhe e ka frenuar veten nga dëshirat",
        "page": 584
      },
      {
        "number": 41,
        "surah": 79,
        "juz": 30,
        "text": "pa dyshim që do të ketë për strehë Xhenetin",
        "translation": "pa dyshim që do të ketë për strehë Xhenetin",
        "page": 584
      },
      {
        "number": 42,
        "surah": 79,
        "juz": 30,
        "text": "Të pyesin ty për Orën (e Kiametit) se kur do të ndodhë",
        "translation": "Të pyesin ty për Orën (e Kiametit) se kur do të ndodhë",
        "page": 584
      },
      {
        "number": 43,
        "surah": 79,
        "juz": 30,
        "text": "Çfarë rëndësie ka për ty që ta përmendësh atë",
        "translation": "Çfarë rëndësie ka për ty që ta përmendësh atë",
        "page": 584
      },
      {
        "number": 44,
        "surah": 79,
        "juz": 30,
        "text": "Tek Zoti yt është fundi i (dijenisë rreth) saj",
        "translation": "Tek Zoti yt është fundi i (dijenisë rreth) saj",
        "page": 584
      },
      {
        "number": 45,
        "surah": 79,
        "juz": 30,
        "text": "Ti je vetëm paralajmërues i atyre që i frikësohen asaj",
        "translation": "Ti je vetëm paralajmërues i atyre që i frikësohen asaj",
        "page": 584
      },
      {
        "number": 46,
        "surah": 79,
        "juz": 30,
        "text": "Ditën kur do ta shohin atë, (njerëzve) do t’u duket se kanë qëndruar (në Tokë) veç një mbrëmje apo një mëngjes",
        "translation": "Ditën kur do ta shohin atë, (njerëzve) do t’u duket se kanë qëndruar (në Tokë) veç një mbrëmje apo një mëngjes",
        "page": 584
      }
    ]
  },
